const axios = require('axios');

const getShopDetails = () => {
    axios.get('http://localhost:1337/categories')
      .then(response => {
        return response.data;
        console.log(response);
      })
      .catch(err => {
        console.log(err)
      })
}

 exports.getShopDetails = getShopDetails;
