const axios = require('axios');

const getShopCategories = async () => {
    return await axios.get('http://localhost:1337/categories')
      .then(response => {
        return response.data;
      })
      .catch(err => {
        console.log(err)
      })
}

 exports.getShopCategories = getShopCategories;
