import React, { useState, useEffect } from 'react'
import Head from 'next/head'
import { getShopCategories } from '../helpers/shop-details';

const Shop = () => {
  const [categories, setCategories] = useState('');

  useEffect(() => {
    const categories = getShopCategories();
    categories.then(result => {
      let categoriesData = {};

      result.data.forEach((item, i) => {
        categoriesData[item.Title] = item.Category_Image.formats.medium.url;
        console.log(categoriesData);
      });

      console.log(categoriesData);

      setCategories(categoriesData);
    })
    .catch(err => {
      console.log(err)
    })

  }, []);

  const setCategoriesDisplay = () => {
    console.log('here are the cats')
    console.log(categories);
    if(categories.length > 0) {
        return categories.map(el => {
          return (
            <option value={el}>{el}</option>
          )
        })
    }
  }

  return (
  <div class="content-area">
      <section class="page-section">
          <div class="container-fluid container-shop">
              <div class="row">
                  <div class="col-md-12 col-lg-12 content" id="content">

                      <div class="shop-sorting">
                          <div class="row">
                              <div class="col-sm-12 text-center">
                                  <span class="hidden-xs">Sort by</span>
                                  <span class="select-wrapper hidden-xs">
                                      <select class='selectClass'>
                                          <option value="name">Product Name</option>
                                          <option value="-price">HIGHEST PRICE</option>
                                          <option value="price">LOWEST PRICE</option>
                                      </select>
                                  </span>

                                  <span>Show me</span>
                                  <span class="select-wrapper">
                                      <select >
                                          <option value="" selected>All Products</option>
                                          {setCategoriesDisplay()}
                                      </select>
                                  </span>
                              </div>
                          </div>
                      </div>

                      <div class="row products grid">
                          <div class="col-md-4 col-sm-6 no-padding-col" id="">
                              <div class="thumbnail no-border no-padding">
                                  <div class="media">
                                      <a class="media-link" href="#/products/{{product.productId}}">
                                          <img alt=""/>
                                      </a>
                                  </div>
                                  <div class="caption text-center">
                                      <h4 class="caption-title">Test</h4>
                                      <div class="price">price goes here</div>
                                  </div>
                              </div>
                          </div>
                      </div>

                  </div>
              </div>
          </div>
      </section>
  </div>
)
}
export default Shop
